// ===================== CATEGORY PRODUCT TYPE =====================
export interface CategoryProduct {
    _id: string;
    name: string;
    images: string[];
    rating: number;
    totalReviews: number;
    price: number;
    originalPrice: number;
    description: string;
    category: string; // category slug
    tag?: 'Factory Price' | 'Best Seller';
    // Filter-related properties
    capacity?: number; // in ml
    height?: number; // in inches
    size?: number; // in inches (for votives)
    diameter?: number; // in inches
    lid?: 'with-lid' | 'without-lid';
    finish?: string;
    shape?: string;
    design?: string;
    setType?: 'single' | 'set-of-2' | 'set-of-3';
    lidDesign?: string;
    occasion?: string;
    stand?: 'yes' | 'no';
    use?: 'decor' | 'serving';
}

