// ===================== GET PRODUCTS BY CATEGORY =====================
export const getCategoryProducts = (categorySlug: string): CategoryProduct[] => {
    switch (categorySlug) {
        case 'best-selling':
            return bestSellingProducts;
        case 'candle-jars':
            return candleJarsProducts;
        case 'votives':
            return votivesProducts;
        case 'gift-hampers':
            return giftHampersProducts;
        case 'bowls-thali':
            return bowlsThaliProducts;
        case 'bell-jars':
            return bellJarsProducts;
        default:
            return [];
    }
};

// Get all category products combined
export const allCategoryProducts: CategoryProduct[] = [
    ...bestSellingProducts,
    ...candleJarsProducts,
    ...votivesProducts,
    ...giftHampersProducts,
    ...bowlsThaliProducts,
    ...bellJarsProducts,
];
